import React from 'react';
import Header from './components/Header/Header.jsx'
import Hero from './components/Hero/Hero.jsx'
import SideVideo from './components/SideVideo/SideVideo'
import MainData from './JSON/main-video-data.json'
import SideData from './JSON/side-video-data.json'


class App extends React.Component{
  state = {
    mainData: MainData[0],
    comments: MainData[0].comments
 }


  submitHandler = (event) => {
    event.preventDefault()
    this.setState(
      {comments: [
        ...this.state.comments, 
        {
          name: "Placeholder",
          comment: event.target.commentBox.value,
          timestamp: new Date().toDateString()
        }]
      })
    event.target.reset()
  }


  render(){
    return (
      <div>
        <Header />
        <Hero submitHandler={event => this.submitHandler(event)} mainVideo={this.state.mainData}/>
        {/* <SideVideo /> */}
      </div>
    );
  }
}

export default App;
