import React from 'react'

import './Description.scss'


function Description({comments}){
  return(
  <div>
    {comments.map((comment, id) => {
      return (
        <div key={id}>
          <h3>{comment.name}</h3>
          <p>{comment.timestamp}</p>
          <p>{comment.comment}</p>
        </div>
      )
    })}
  </div>

  )
}


export default Description;
