import React from 'react'
import './Hero.scss'
import MainData from '../../JSON/main-video-data.json'
import SideData from '../../JSON/side-video-data.json'

class Hero extends React.Component {





  render() {
    return(
      <div className="hero">

        <video 
        className="hero__img" 
        src={process.env.PUBLIC_URL + this.state.mainData.video} 
        poster={process.env.PUBLIC_URL + this.state.mainData.image} 
        alt="Hero img" 
        controls
        />

        <div>
          <h1>{this.state.mainData.title}</h1>
          <h3>By {this.state.mainData.channel}</h3>
          <p>{this.state.mainData.timestamp}</p>
          <p>{this.state.mainData.views}</p>
          <p>{this.state.mainData.likes}</p>
          <p>{this.state.mainData.description}</p>
        </div>

        <div>
          <h3>3 Comments</h3>
          <form onSubmit={this.submitHandler}>
            <textarea name="commentBox" cols="30" rows="10"></textarea>
            <button>COMMENT</button>
          </form>
        </div>

        <div>
          {this.state.comments.map((comment, id) => {
            return (
              <div key={id}>
                <h3>{comment.name}</h3>
                <p>{comment.timestamp}</p>
                <p>{comment.comment}</p>
              </div>
            )
          })}
        </div>
      </div>
    )
  }
}

export default Hero;
