import React from 'react'
import './Hero.scss'

function Hero({mainData}) {
    return(
      <div className="hero"> 
        <video 
        className="hero__vid" 
        src={process.env.PUBLIC_URL + mainData.video} 
        poster={process.env.PUBLIC_URL + mainData.image} 
        alt="Hero img" 
        controls
        />
        <div className="hero__vid-controls">

        </div>
      </div>
    )
}

export default Hero;
