import React from 'react'
import './Hero.scss'
import MainData from '../../JSON/main-video-data.json'
import SideData from '../../JSON/side-video-data.json'

class Hero extends React.Component {
  state = {MainData, SideData}



  // console.log("hi")
  // let data = {MainData}
  // console.log(data.MainData[0].description)



  render() {
    return(
      <div className="hero">
        <img src={process.env.PUBLIC_URL + this.state.MainData[0].image} alt="Hero img" className="hero__img"/>
        <div className="hero__controls">
          {/* <img src={PlayBtn} alt="Play button"/> */}
        </div>
        <p>{this.state.MainData[0].description}</p>
        <p>{this.state.MainData[0].image}</p>
      </div>
    )
  }
}

export default Hero;
